import React,{useState} from 'react'
import sample from "../Assets/samplegates.png"
import { NavLink } from 'react-router-dom'
const About = () => {
  const [dropdownOpen, setDropdownOpen] = useState(false);

  const toggleDropdown = () => {
    setDropdownOpen(!dropdownOpen);
  };
  return (
    <div className='w-screen h-screen'>
      <div className='w-screen h-12 bg-primary p-2 text-white font-semibold text-lg flex flex-row space-x-2'>
<NavLink to="/">About</NavLink> 
<h1>{'>'}</h1>
<button
            className="text-white focus:outline-none"
            onClick={toggleDropdown}
          >
            Affiliation
          </button>

          {dropdownOpen && (
            <div className="absolute bg-gray-800 mt-2 p-2 rounded">
              <a href="#" className="block text-white py-2">
                Suvo
              </a>
              <a href="#" className="block text-white py-2">
                Contact Us
              </a>
            </div>
          )}
      </div>
      <img src = {sample} alt="" className='w-screen h-[800px] '/>

    </div>
  )
}

export default About
