import React, { useState } from 'react';
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { NavLink } from 'react-router-dom';

const Dropdown = ({value}) => {
  const [anchorEl, setAnchorEl] = useState(null);
  const [selectedItem, setSelectedItem] = useState("Affiliation"); // Default selected item
  const open = Boolean(anchorEl);

  const handleClick = (event) => {
    setAnchorEl(event.currentTarget);
  };

  const handleClose = () => {
    setAnchorEl(null);
  };

  const handleMenuItemClick = (item) => {
    setSelectedItem(item);
    handleClose();
  };

  return (
    <div className="w-screen h-12 bg-primary p-2 text-white font-semibold text-lg flex flex-row space-x-2">
      <NavLink to="/">About</NavLink>
      <h1>{">"}</h1>
      <button onClick={handleClick}>{value}</button>
      <Menu
        id="basic-menu"
        anchorEl={anchorEl}
        open={open}
        onClose={handleClose}
        MenuListProps={{
          "aria-labelledby": "basic-button",
        }}
      >
        <MenuItem onClick={() => handleMenuItemClick('Affiliation')}>
          <NavLink to="/affiliation">Affiliation</NavLink>
        </MenuItem>
        <MenuItem onClick={() => handleMenuItemClick('Suvo Chaterjee')}>
          <NavLink to="/suvo">Suvo Chaterjee</NavLink>
        </MenuItem>
        <MenuItem onClick={() => handleMenuItemClick('Contact Us')}>
          <NavLink to="/contact">Contact Us</NavLink>
        </MenuItem>
      </Menu>
    </div>
  );
}

export default Dropdown;
