import React from 'react'
import Menu from "@mui/material/Menu";
import MenuItem from "@mui/material/MenuItem";
import { NavLink } from 'react-router-dom';
const Dropdown = ({value}) => {
    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const handleClick = (event) => {
      setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
      setAnchorEl(null);
    };
  return (
    <div className="w-screen h-12 bg-primary p-2 text-white font-semibold text-lg flex flex-row space-x-2">
    <NavLink to="/">About</NavLink>
    <h1>{"|"}</h1>
    <button className="shadow-sm shadow-secondary rounded-md w-[200px]"  onClick={handleClick}>{value}</button>
    <Menu
      id="basic-menu"
      anchorEl={anchorEl}
      open={open}
      onClose={handleClose}
      MenuListProps={{
        "aria-labelledby": "basic-button",
      }}
    >
      <MenuItem onClick={handleClose}>
        <NavLink to="/affiliation"> Affiliation</NavLink>
      </MenuItem>
      <MenuItem onClick={handleClose}>
        <NavLink to="/suvo"> Suvo Chaterjee</NavLink>
      </MenuItem>
      <MenuItem onClick={handleClose}>
        <NavLink to="/contact"> Contact Us</NavLink>
      </MenuItem>
    </Menu>
  </div>


  )
}

export default Dropdown
